import axios from 'axios';

interface Review {
  reviewer: string;
  review: string;
}

interface Whisky {
  id: string;
  name: string;
  image: string;
  description: string;
  origin: string;
  distiller: string;
  type: string;
  flavour: string;
  reviews: Review[];
}

const API_BASE_URL = 'http://localhost:8080/api/whisky';

export const getAllWhiskies = async () => {
  try {
    const response = await axios.get(API_BASE_URL);
    return response.data;
  } catch (error) {
    console.error('There was an error fetching the whiskies!', error);
    if (axios.isAxiosError(error) && error.response) {
      console.error('Response error:', error.response.data);
    }
    throw error;
  }
};

export const getWhiskyById = async (id: string) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/${id}`);
    return response.data;
  } catch (error) {
    console.error('There was an error fetching the whisky details!', error);
    if (axios.isAxiosError(error) && error.response) {
      console.error('Response error:', error.response.data);
    }
    throw error;
  }
};
