import React from "react";
import './style.css';
import { WhiskyList } from '../../type/interface';
import { useNavigate } from 'react-router-dom';

interface Props {
    whiskyItem: WhiskyList
}
// 위스키 리스트 컴포넌트
export default function WhiskyItem({ whiskyItem }: Props) {

    // properties
    const { whiskyNumber, title, content, whiskyMainImage, likesCount, commentCount, writeDatetime } = whiskyItem;

    // function: 네비게이트 함수
    const navigator = useNavigate();

    // event handler
    const onClickHandler = () => {
        navigate(whiskyNumber);
    }

    // 위스키 리스트 컴포넌트 렌더링
    return (
        <div className='whisky-item'>
            <div className='whisky-item-image-box'>
                    <div className='whisky-item-image-content' style={{ backgroundImage: `url(${whiskyMainImage})` }}></div>
            </div>
            <div className='whisky-item-main-box'>
                <div className='whisky-item-mid'>
                        <div className='whisky-item-title'>{title}</div>
                        <div className='whisky-item-content'>{content}</div>
                </div>
                <div className='whisky-item-bot'>
                    <div className='whisky-item-counts'>{`좋아요 ${likesCount}`}</div>
                </div>
            </div>

        </div>
    )
}
