// src/components/WhiskyList.tsx
import React, { useEffect, useState } from 'react';
import WhiskyCard from './WhiskyCard';
import { getAllWhiskies } from '/apis/whiskySearch';
import './components.css';

interface Whisky {
  id: string;
  name: string;
  image: string;
  origin: string;
  distiller: string;
  type: string;
  flavour: string;
}

interface WhiskyListProps {
  searchTerm: string;
  searchCategory: string;
}

const WhiskyList: React.FC<WhiskyListProps> = ({ searchTerm, searchCategory }) => {
  const [whiskies, setWhiskies] = useState<Whisky[]>([]);
  const [filteredWhiskies, setFilteredWhiskies] = useState<Whisky[]>([]);

  useEffect(() => {
    const fetchWhiskies = async () => {
      try {
        const whiskyData = await getAllWhiskies();
        const formattedData = whiskyData.map((whisky: any) => ({
          id: whisky.whiskyID,
          name: whisky.whiskyName,
          image: '/path/to/default/image.jpg', // Adjust as necessary
          origin: whisky.whiskyOrigin,
          distiller: whisky.whiskyDistiller,
          type: whisky.whiskyType,
          flavour: whisky.whiskyFlavour
        }));
        setWhiskies(formattedData);
        setFilteredWhiskies(formattedData);
      } catch (error) {
        console.error('Error fetching whiskies:', error);
      }
    };

    fetchWhiskies();
  }, []);

  useEffect(() => {
    if (searchTerm) {
      setFilteredWhiskies(
        whiskies.filter(whisky =>
          whisky.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
          whisky.origin.toLowerCase().includes(searchTerm.toLowerCase()) ||
          whisky.distiller.toLowerCase().includes(searchTerm.toLowerCase()) ||
          whisky.type.toLowerCase().includes(searchTerm.toLowerCase()) ||
          whisky.flavour.toLowerCase().includes(searchTerm.toLowerCase())
        )
      );
    } else {
      setFilteredWhiskies(whiskies);
    }
  }, [searchTerm, whiskies]);

  return (
    <div className="whisky-list">
      {filteredWhiskies.length > 0 ? (
        filteredWhiskies.map(whisky => (
          <WhiskyCard key={whisky.id} whisky={whisky} />
        ))
      ) : (
        <p>No whiskies found</p>
      )}
    </div>
  );
};

export default WhiskyList;
