import React from 'react';
import './components.css';

interface Review {
  reviewer: string;
  review: string;
}

interface Whisky {
  id: string;
  name: string;
  image: string;
  description: string;
  origin: string;
  distiller: string;
  type: string;
  flavour: string;
  reviews: Review[];
}

interface WhiskyDescProps {
  whisky: Whisky;
}

const WhiskyDesc: React.FC<WhiskyDescProps> = ({ whisky }) => {
  return (
    <div className="whisky-page">
      <div className="whisky-header">
        <h1>{whisky.name}</h1>
        <div className="whisky-header-details">
          <p><strong>Origin:</strong> {whisky.origin}</p>
          <p><strong>Distiller:</strong> {whisky.distiller}</p>
          <p><strong>Type:</strong> {whisky.type}</p>
          <p><strong>Flavour:</strong> {whisky.flavour}</p>
        </div>
      </div>
      <div className="whisky-main-image">
        <img src={whisky.image} alt={whisky.name} />
      </div>
      <div className="whisky-description">
        <h2>Description</h2>
        <p>{whisky.description}</p>
      </div>
      <div className="whisky-reviews">
        <h2>Reviews</h2>
        {whisky.reviews.length > 0 ? (
          whisky.reviews.map((review, index) => (
            <div key={index} className="whisky-review">
              <h3>{review.reviewer}</h3>
              <p>{review.review}</p>
            </div>
          ))
        ) : (
          <p>No reviews yet.</p>
        )}
      </div>
    </div>
  );
};

export default WhiskyDesc;
