// src/components/WhiskyDetail.tsx
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getWhiskyById } from '../apis/WhiskySearch';
import './components.css';

interface Whisky {
  id: string;
  name: string;
  image: string;
  description: string;
  origin: string;
  distiller: string;
  type: string;
  flavour: string;
  rating: number;
  reviews: Array<{ reviewer: string; review: string }>;
}

const WhiskyDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [whisky, setWhisky] = useState<Whisky | null>(null);

  useEffect(() => {
    if (!id) {
      navigate('/'); // Redirect to home if no id is present
      return;
    }

    const fetchWhisky = async () => {
      try {
        const whiskyData = await getWhiskyById(id);
        setWhisky({
          id: whiskyData.whiskyID,
          name: whiskyData.whiskyName,
          image: '/path/to/default/image.jpg', // Adjust as necessary
          description: whiskyData.description,
          origin: whiskyData.whiskyOrigin,
          distiller: whiskyData.whiskyDistiller,
          type: whiskyData.whiskyType,
          flavour: whiskyData.whiskyFlavour,
          rating: whiskyData.Whiating,
          reviews: whiskyData.reviews || []
        });
      } catch (error) {
        console.error('Error fetching whisky details:', error);
      }
    };

    fetchWhisky();
  }, [id, navigate]);

  if (!whisky) return <div>Loading...</div>;

  return (
    <div className="whisky-detail-page">
      <div className="whisky-detail-header">
        <img src={whisky.image} alt={whisky.name} />
        <div className="whisky-basic-info">
          <h1>{whisky.name}</h1>
          <p>{whisky.origin}</p>
          <p>{whisky.distiller}</p>
          <p>{whisky.type}</p>
          <p>{whisky.flavour}</p>
          <div className="whisky-rating">
            <span>Rating: {whisky.rating}</span>
          </div>
        </div>
      </div>
      <div className="whisky-description">
        <h2>Description</h2>
        <p>{whisky.description}</p>
      </div>
      <div className="whisky-reviews">
        <h2>Reviews</h2>
        {whisky.reviews.length > 0 ? (
          whisky.reviews.map((review, index) => (
            <div key={index} className="whisky-review">
              <h3>{review.reviewer}</h3>
              <p>{review.review}</p>
            </div>
          ))
        ) : (
          <p>No reviews yet.</p>
        )}
      </div>
    </div>
  );
};

export default WhiskyDetail;
