// src/components/WhiskyDetail.tsx
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './components.css';


interface Whisky {
  id: number;
  name: string;
  image: string;
  description: string;
  region: string;
  rating: number;
}

const WhiskyDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [whisky, setWhisky] = useState<Whisky | null>(null);

  useEffect(() => {
    axios.get(`/api/whiskies/${id}`)
      .then(response => {
        setWhisky(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the whisky details!', error);
      });
  }, [id]);

  if (!whisky) return <div>Loading...</div>;

  return (
    <div className="whisky-detail">
      <h2>{whisky.name}</h2>
      <img src={whisky.image} alt={whisky.name} />
      <p>{whisky.description}</p>
      <p><strong>Region:</strong> {whisky.region}</p>
      <p><strong>Rating:</strong> {whisky.rating}</p>
      <h3>Reviews</h3>
      {/* Add review form and display reviews here */}
    </div>
  );
};

export default WhiskyDetail;
