interface SignInData {
  userEmail: string;
  userPassword: string;
}

interface RegisterData {
  userEmail: string;
  userName: string;
  userPassword: string;
  userPwConfirm: string;
}

interface UserProfileData {
  userEmail: string;
  userName: string;
}

const apiUrl = 'http://localhost:8080/api/auth'; // Update with your actual API URL

export const signIn = async (signInData: SignInData): Promise<Response> => {
  try {
    const response = await fetch(`${apiUrl}/signin`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(signInData),
    });

    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.message || 'Failed to sign in');
    }

    return response;
  } catch (error) {
    console.error('Error during sign in:', error.message);
    throw error; // Rethrow the error to propagate it further
  }
};

export const register = async (registerData: RegisterData): Promise<Response> => {
  try {
    const response = await fetch(`${apiUrl}/signUp`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(registerData),
    });

    if (!response.ok) {
      throw new Error('Failed to register');
    }

    return response;
  } catch (error: any) { // Explicitly specify 'any' type for error
    console.error('Error during registration:', error.message);
    throw error; // Rethrow the error to propagate it further
  }
};

export const getUserProfile = async (): Promise<UserProfileData> => {
  const token = localStorage.getItem('token');

  if (!token) {
    throw new Error('No token found');
  }

  try {
    const response = await fetch(`${apiUrl}/profile`, {
      method: 'GET',
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json',
      },
    });

    if (!response.ok) {
      throw new Error('Failed to fetch user profile');
    }

    const data: UserProfileData = await response.json();
    return data;
  } catch (error: any) {
    console.error('Error fetching user profile:', error.message);
    throw error;
  }
};
