import React, { useState } from 'react';
import './layouts.css';

interface UserProfileProps {
  userName: string;
  onSignOut: () => void;
}

const UserProfile: React.FC<UserProfileProps> = ({ userName, onSignOut }) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className="user-profile">
      <button onClick={toggleDropdown} className="profile-button">
        <img src="path-to-profile-picture" alt="User Profile" className="profile-picture" />
      </button>
      {isOpen && (
        <div className="profile-dropdown">
          <ul>
            <li><a href="/trips">Trips</a></li>
            <li><a href="/write-review">Write a review</a></li>
            <li><a href="/profile">Profile</a></li>
            <li><a href="/bookings">Bookings</a></li>
            <li><a href="/notifications">Notifications</a></li>
            <li><a href="/account-info">Account info</a></li>
            <li><button onClick={onSignOut}>Sign out</button></li>
          </ul>
        </div>
      )}
    </div>
  );
};

export default UserProfile;
