// src/components/Header.tsx
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import SignInModal from '../components/Auth/SignInModal';
import RegisterModal from '../components/Auth/RegisterModal';
import './components.css';

const Header: React.FC = () => {
  const [isSignInOpen, setSignInOpen] = useState(false);
  const [isRegisterOpen, setRegisterOpen] = useState(false);

  const handleSignInOpen = () => {
    setSignInOpen(true);
    setRegisterOpen(false);
  };

  const handleRegisterOpen = () => {
    setRegisterOpen(true);
    setSignInOpen(false);
  };

  const handleClose = () => {
    setSignInOpen(false);
    setRegisterOpen(false);
  };

  return (
    <header className="header">
      <div className="logo">WhiskyReviews</div>
      <nav>
        <ul>
          <li><Link to="/">Home</Link></li>
          <li><Link to="/discover">Discover</Link></li>
          <li><Link to="/trips">Trips</Link></li>
          <li><Link to="/review">Review</Link></li>
          <li><Link to="/more">More</Link></li>
        </ul>
      </nav>
      <div className="header-right">
        <span>USD</span>
        <button className="sign-in-btn" onClick={handleSignInOpen}>Sign in</button>
      </div>
      <SignInModal isOpen={isSignInOpen} onClose={handleClose} onSwitchToRegister={handleRegisterOpen} />
      <RegisterModal isOpen={isRegisterOpen} onClose={handleClose} onSwitchToSignIn={handleSignInOpen} />
    </header>
  );
};

export default Header;
