import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { register } from '../../apis/authApi';
import './auth.css';

interface RegisterModalProps {
  onClose: () => void;
  onSwitchToSignIn: () => void;
}

const RegisterModal: React.FC<RegisterModalProps> = ({ onClose, onSwitchToSignIn }) => {
  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const navigate = useNavigate();

  const handleRegister = async () => {
    if (password !== confirmPassword) {
      alert('Passwords do not match');
      return;
    }
    try {
      await register({ userEmail: email, userName: username, userPassword: password, userPwConfirm: confirmPassword });
      onClose();
      navigate('/signin'); // Redirect to sign-in page
    } catch (error) {
      console.error('Registration failed', error);
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="close-button" onClick={onClose}>
          &times;
        </button>
        <h2>Create an account</h2>
        <form
          onSubmit={(e) => {
            e.preventDefault();
            handleRegister();
          }}
        >
          <label>Email address</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <label>Username</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <label>Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <label>Confirm Password</label>
          <input
            type="password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            required
          />
          <button className="modal-button" type="submit">
            Register
          </button>
        </form>
        <p>
          Already have an account? <button className="switch-button" onClick={onSwitchToSignIn}>Sign in</button>
        </p>
      </div>
    </div>
  );
};

export default RegisterModal;
