// src/components/WhiskyDetail.tsx
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './components.css';

interface Whisky {
  id: string;
  name: string;
  image: string;
  description: string;
  origin: string;
  distiller: string;
  type: string;
  flavour: string;
  rating: number;
}

const WhiskyDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [whisky, setWhisky] = useState<Whisky | null>(null);

  useEffect(() => {
    axios.get(`/api/whiskies/${id}`)
      .then(response => {
        const whiskyData = response.data;
        setWhisky({
          id: whiskyData.whiskyID,
          name: whiskyData.whiskyName,
          image: '/path/to/default/image.jpg', // Adjust as necessary
          description: whiskyData.description,
          origin: whiskyData.whiskyOrigin,
          distiller: whiskyData.whiskyDistiller,
          type: whiskyData.whiskyType,
          flavour: whiskyData.whiskyFlavour,
          rating: whiskyData.rating
        });
      })
      .catch(error => {
        console.error('There was an error fetching the whisky details!', error);
      });
  }, [id]);

  if (!whisky) return <div>Loading...</div>;

  return (
    <div className="whisky-detail">
      <h2>{whisky.name}</h2>
      <img src={whisky.image} alt={whisky.name} />
      <p>{whisky.description}</p>
      <p><strong>Origin:</strong> {whisky.origin}</p>
      <p><strong>Distiller:</strong> {whisky.distiller}</p>
      <p><strong>Type:</strong> {whisky.type}</p>
      <p><strong>Flavour:</strong> {whisky.flavour}</p>
      <p><strong>Rating:</strong> {whisky.rating}</p>
      <h3>Reviews</h3>
      {/* Add review form and display reviews here */}
    </div>
  );
};

export default WhiskyDetail;
