// src/pages/WhiskyPage.tsx
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getWhiskyById } from '../apis/WhiskySearch';
import WhiskyDesc from '../components/WhiskyDesc';
import './pages.css';

interface Review {
  reviewer: string;
  review: string;
}

interface Whisky {
  id: string;
  name: string;
  image: string;
  description: string;
  origin: string;
  distiller: string;
  type: string;
  flavour: string;
  rating: number;
  reviews: Review[];
  images: string[];
  ratingDetails: {
    location: number;
    cleanliness: number;
    service: number;
    value: number;
  };
  amenities: string[];
  roomFeatures: string[];
  roomTypes: string[];
}

const WhiskyPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [whisky, setWhisky] = useState<Whisky | null>(null);

  useEffect(() => {
    if (!id) {
      navigate('/'); // Redirect to home if no id is present
      return;
    }

    const fetchWhisky = async () => {
      try {
        const whiskyData = await getWhiskyById(id);
        setWhisky({
          id: whiskyData.whiskyID,
          name: whiskyData.whiskyName,
          image: '/path/to/default/image.jpg', // Adjust as necessary
          description: whiskyData.description,
          origin: whiskyData.whiskyOrigin,
          distiller: whiskyData.whiskyDistiller,
          type: whiskyData.whiskyType,
          flavour: whiskyData.whiskyFlavour,
          rating: whiskyData.rating,
          reviews: whiskyData.reviews || [],
          images: whiskyData.images || [], // Assuming an array of images
          ratingDetails: whiskyData.ratingDetails || {},
          amenities: whiskyData.amenities || [],
          roomFeatures: whiskyData.roomFeatures || [],
          roomTypes: whiskyData.roomTypes || [],
        });
      } catch (error) {
        console.error('Error fetching whisky details:', error);
      }
    };

    fetchWhisky();
  }, [id, navigate]);

  if (!whisky) return <div>Loading...</div>;

  return <WhiskyDesc whisky={whisky} />;
};

export default WhiskyPage;
