// src/components/Auth/SignInModal.tsx
import React, { useState } from 'react';
import { signIn } from 'apis/Authorisation';
import './auth.css';

interface SignInModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSwitchToRegister: () => void;
}

const SignInModal: React.FC<SignInModalProps> = ({ isOpen, onClose, onSwitchToRegister }) => {
  const [userEmail, setUserEmail] = useState('');
  const [userPassword, setUserPassword] = useState('');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      await signIn({ userEmail, userPassword });
      onClose();
    } catch (error) {
      console.error('Sign in error', error);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="close-button" onClick={onClose}>Ã—</button>
        <h2>Welcome back.</h2>
        <form onSubmit={handleSubmit}>
          <label>Email address</label>
          <input type="email" value={userEmail} onChange={(e) => setUserEmail(e.target.value)} required />
          <label>Password</label>
          <input type="password" value={userPassword} onChange={(e) => setUserPassword(e.target.value)} required />
          <button type="submit" className="modal-button">Sign in</button>
        </form>
        <p>
          <a href="#">Forgot password?</a>
        </p>
        <p>
          Not a member? <button className="switch-button" onClick={onSwitchToRegister}>Join</button>
        </p>
      </div>
    </div>
  );
};

export default SignInModal;
