// src/components/SearchBar.tsx
import React, { useState } from 'react';
import './components.css';

const SearchBar: React.FC = () => {
  const [searchCategory, setSearchCategory] = useState('Search All');

  return (
    <div className="search-bar">
      <h1>위스키 한 잔?</h1>
      <div className="search-categories">
        <button onClick={() => setSearchCategory('Search All')}>전체 검색</button>
        <button onClick={() => setSearchCategory('Whisky')}>위스키</button>
        <button onClick={() => setSearchCategory('Distiller')}>증류소</button>
        <button onClick={() => setSearchCategory('Origin')}>지역</button>
        <button onClick={() => setSearchCategory('Type')}>종류</button>
        <button onClick={() => setSearchCategory('Taste')}>맛/향기</button>
      </div>
      <div className="search-input">
        <input type="text" placeholder={`Search ${searchCategory.toLowerCase()}...`} />
        <button className="search-btn">Search</button>
      </div>
    </div>
  );
};

export default SearchBar;
