import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getWhiskyById } from '../apis/WhiskySearch';
import WhiskyDesc from '../components/WhiskyDesc';
import './pages.css';

interface Review {
  reviewer: string;
  review: string;
}

interface Whisky {
  id: string;
  name: string;
  image: string;
  description: string;
  origin: string;
  distiller: string;
  type: string;
  flavour: string;
  reviews: Review[];
  }

  const WhiskyPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [whisky, setWhisky] = useState<Whisky | null>(null);

  useEffect(() => {
  if (!id) {
  navigate('/'); // Redirect to home if no id is present
  return;
  } const fetchWhisky = async () => {
    try {
      const whiskyData = await getWhiskyById(id);
      setWhisky({
        id: whiskyData.whiskyID,
        name: whiskyData.whiskyName,
        image: whiskyData.whiskyImage || '/path/to/default/image.jpg', // Adjust as necessary
        description: whiskyData.whiskyDescription,
        origin: whiskyData.whiskyOrigin,
        distiller: whiskyData.whiskyDistiller,
        type: whiskyData.whiskyType,
        flavour: whiskyData.whiskyFlavour,
        reviews: whiskyData.reviews || [],
      });
    } catch (error) {
      console.error('Error fetching whisky details:', error);
    }
  };

  fetchWhisky();}, [id, navigate]);

  if (!whisky) return 'Loading…';

  return ;
  };

  export default WhiskyPage;
