import React, { useState } from 'react';
import { useAuth } from '../../context/AuthContext';
import { signIn } from '../../apis/Authorisation';
import './auth.css';

interface SignInModalProps {
  onClose: () => void;
  onSwitchToRegister: () => void;
}

const SignInModal: React.FC<SignInModalProps> = ({ onClose, onSwitchToRegister }) => {
  const { setUser } = useAuth();
  const [userEmail, setEmail] = useState('');
  const [userPassword, setPassword] = useState('');
  const [error, setError] = useState<string | null>(null); // Add state for error message

  const handleSignIn = async () => {
    try {
      const response = await signIn({ userEmail: email, userPassword: password });
      if (response.ok) {
        const data = await response.json();
        setUser(data);
        localStorage.setItem('user', JSON.stringify(data));
        onClose();
      } else {
        setError('Failed to sign in. Please check your email and password.'); // Set error message
      }
    } catch (error) {
      console.error('Sign-in failed', error);
      setError('An error occurred during sign in. Please try again.'); // Set error message
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="close-button" onClick={onClose}>
          &times;
        </button>
        <h2>Welcome back.</h2>
        {error && <p className="error-message">{error}</p>} {/* Conditionally render error message */}
        <form
          onSubmit={(e) => {
            e.preventDefault();
            handleSignIn();
          }}
        >
          <label>Email address</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <label>Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button className="modal-button" type="submit">
            Sign in
          </button>
        </form>
        <p>
          Don't have an account? <button className="switch-button" onClick={onSwitchToRegister}>Register</button>
        </p>
      </div>
    </div>
  );
};

export default SignInModal;
