// src/components/Auth/RegisterModal.tsx
import React, { useState } from 'react';
import { register } from './register';
import './auth.css';

interface RegisterModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSwitchToSignIn: () => void;
}

const RegisterModal: React.FC<RegisterModalProps> = ({ isOpen, onClose, onSwitchToSignIn }) => {
  const [userEmail, setUserEmail] = useState('');
  const [userName, setUserName] = useState('');
  const [userPassword, setUserPassword] = useState('');
  const [userPwConfirm, setUserPwConfirm] = useState('');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      await register({ userEmail, userName, userPassword, userPwConfirm });
      onClose();
    } catch (error) {
      console.error('Registration error', error);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="close-button" onClick={onClose}>Ã—</button>
        <h2>Join to unlock the best of WhiskyReviews</h2>
        <form onSubmit={handleSubmit}>
          <label>Email address</label>
          <input type="email" value={userEmail} onChange={(e) => setUserEmail(e.target.value)} required />
          <label>Full Name</label>
          <input type="text" value={userName} onChange={(e) => setUserName(e.target.value)} required />
          <label>Create a password</label>
          <input type="password" value={userPassword} onChange={(e) => setUserPassword(e.target.value)} required />
          <label>Confirm password</label>
          <input type="password" value={userPwConfirm} onChange={(e) => setUserPwConfirm(e.target.value)} required />
          <button type="submit" className="modal-button">Join</button>
        </form>
        <p>
          Already a member? <button className="switch-button" onClick={onSwitchToSignIn}>Sign in</button>
        </p>
      </div>
    </div>
  );
};

export default RegisterModal;
