import React, { useState } from 'react';
import { signIn } from 'apis/Authorisation';
import './auth.css';

interface SignInModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSwitchToRegister: () => void;
}

const SignInModal: React.FC<SignInModalProps> = ({ isOpen, onClose, onSwitchToRegister }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSignIn = async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      const response = await signIn({ email, password });

      if (response.ok) {
        console.log('Login successful');
        onClose(); // Close modal after successful login
      } else {
        console.error('Login failed');
      }
    } catch (error) {
      console.error('Error during login:', error);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="close-button" onClick={onClose}>Ã—</button>
        <h2>Welcome back.</h2>
        <form onSubmit={handleSignIn}>
          <label>Email address</label>
          <input
            type="email"
            required
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <label>Password</label>
          <input
            type="password"
            required
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button type="submit" className="modal-button">Sign in</button>
        </form>
        <p>
          <a href="#">Forgot password?</a>
        </p>
        <p>
          Not a member? <button className="switch-button" onClick={onSwitchToRegister}>Join</button>
        </p>
      </div>
    </div>
  );
};

export default SignInModal;
