// src/components/WhiskyDesc.tsx
import React from 'react';
import { Link } from 'react-router-dom';
import './components.css'; // Ensure to create and import a CSS file for specific styles

interface Review {
  reviewer: string;
  review: string;
}

interface Whisky {
  id: string;
  name: string;
  image: string;
  description: string;
  origin: string;
  distiller: string;
  type: string;
  flavour: string;
  // rating: number;
  reviews: Review[];
  // images: string[];
  // ratingDetails: {
  //   location: number;
  //   cleanliness: number;
  //   service: number;
  //   value: number;
  // };
}

interface WhiskyDescProps {
  whisky: Whisky;
}

const WhiskyDesc: React.FC<WhiskyDescProps> = ({ whisky }) => {
  return (
    <div className="whisky-page">
      <div className="whisky-header">
        <h1>{whisky.name}</h1>
        <div className="whisky-header-details">
          <p>{whisky.origin}</p>
          <p>{whisky.distiller}</p>
          <p>{whisky.type}</p>
          <p>{whisky.flavour}</p>
          {/* <span className="whisky-rating">Rating: {whisky.rating}</span> */}
        </div>
      </div>
      <div className="whisky-main-image">
        {/* <img src={whisky.image} alt={whisky.name} /> */}
      </div>
      <div className="whisky-description">
        <h2>Description</h2>
        {/* <p>{whisky.description}</p> */}
      </div>
      <div className="whisky-details">
        <h2>Details</h2>
        <div className="whisky-details-section">
          <h3>Rating Details</h3>
          {/* <p>Location: {whisky.ratingDetails.location}</p>
          <p>Cleanliness: {whisky.ratingDetails.cleanliness}</p>
          <p>Service: {whisky.ratingDetails.service}</p>
          <p>Value: {whisky.ratingDetails.value}</p> */}
        </div>
        <div className="whisky-details-section">
          {/* <h3>Amenities</h3>
          <ul>
            {whisky.amenities.map((amenity, index) => (
              <li key={index}>{amenity}</li>
            ))}
          </ul> */}
        </div>
        <div className="whisky-details-section">
          {/* <h3>Room Features</h3>
          <ul>
            {whisky.roomFeatures.map((feature, index) => (
              <li key={index}>{feature}</li>
            ))}
          </ul> */}
        </div>
        <div className="whisky-details-section">
          {/* <h3>Room Types</h3>
          <ul>
            {whisky.roomTypes.map((type, index) => (
              <li key={index}>{type}</li>
            ))}
          </ul> */}
        </div>
      </div>
      <div className="whisky-reviews">
        <h2>Reviews</h2>
        {whisky.reviews.length > 0 ? (
          whisky.reviews.map((review, index) => (
            <div key={index} className="whisky-review">
              <h3>{review.reviewer}</h3>
              <p>{review.review}</p>
            </div>
          ))
        ) : (
          <p>No reviews yet.</p>
        )}
      </div>
      <div className="whisky-images">
        {/* <h2>Additional Images</h2>
        <div className="whisky-image-gallery">
          {whisky.images.map((img, index) => (
            <img key={index} src={img} alt={`whisky ${index}`} />
          ))}
        </div> */}
      </div>
    </div>
  );
};

export default WhiskyDesc;
