import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { register } from '../../apis/Authorisation';
import './auth.css';

interface RegisterModalProps {
  onClose: () => void;
  onSwitchToSignIn: () => void;
}

const RegisterModal: React.FC<RegisterModalProps> = ({ onClose, onSwitchToSignIn }) => {
  const [userEmail, setEmail] = useState('');
  const [userName, setUsername] = useState('');
  const [userPassword, setPassword] = useState('');
  const [userPwConfirm, setConfirmPassword] = useState('');
  const [error, setError] = useState<string | null>(null); // Add state for error message

  const navigate = useNavigate();

  const handleRegister = async () => {
    setError(null); // Clear any existing errors
    if (userPassword !== userPwConfirm) {
      setError('Passwords do not match');
      return;
    }
    try {
      const response = await register({
        userEmail: userEmail,
        userName: userName,
        userPassword: userPassword,
        userPwConfirm: userPwConfirm,
      });
      if (response.ok) {
        onClose();
        navigate('/signin'); // Redirect to sign-in page
      } else {
        const data = await response.json();
        setError(data.message || 'Registration failed'); // Set error message from server response
      }
    } catch (error) {
      console.error('Registration failed', error);
      setError('An error occurred during registration. Please try again.'); // Set generic error message
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="close-button" onClick={onClose}>
          &times;
        </button>
        <h2>Create an account</h2>
        {error && <p className="error-message">{error}</p>} {/* Conditionally render error message */}
        <form
          onSubmit={(e) => {
            e.preventDefault();
            handleRegister();
          }}
        >
          <label>Email address</label>
          <input
            type="email"
            value={userEmail}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <label>Username</label>
          <input
            type="text"
            value={userName}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <label>Password</label>
          <input
            type="password"
            value={userPassword}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <label>Confirm Password</label>
          <input
            type="password"
            value={userPwConfirm}
            onChange={(e) => setConfirmPassword(e.target.value)}
            required
          />
          <button className="modal-button" type="submit">
            Register
          </button>
        </form>
        <p>
          Already have an account? <button className="switch-button" onClick={onSwitchToSignIn}>Sign in</button>
        </p>
      </div>
    </div>
  );
};

export default RegisterModal;
