import React, { useState, useEffect } from 'react';
import { getUserProfile } from 'apis/Authorisation';
import './auth.css';

const UserProfile: React.FC = () => {
  const [userProfile, setUserProfile] = useState<{ userEmail: string; userName: string } | null>(null);

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const profile = await getUserProfile();
        setUserProfile(profile);
      } catch (error) {
        console.error('Failed to fetch user profile:', error);
      }
    };

    fetchUserProfile();
  }, []);

  return (
    <div className="user-profile">
      {userProfile ? (
        <div className="profile-dropdown">
          <button className="profile-button">
            <img src="path/to/user/profile/image.jpg" alt="User Profile" className="profile-image" />
            <span>{userProfile.userName}</span>
          </button>
          <div className="profile-menu">
            <ul>
              <li>Trips</li>
              <li>Write a review</li>
              <li>Profile</li>
              <li>Bookings</li>
              <li>Notifications</li>
              <li>Account info</li>
              <li>Sign out</li>
            </ul>
          </div>
        </div>
      ) : (
        <p>Loading profile...</p>
      )}
    </div>
  );
};

export default UserProfile;
